mapper.select:
SELECT oons.order_code, oons.order_latest_status, oons.status_update_time, oons.status_create_time FROM ofc_order_newstatus oons LEFT JOIN ofc_fundamental_information ofi ON oons.order_code = ofi.order_code LEFT JOIN ofc_distribution_basic_info odbi ON oons.order_code = odbi.order_code
OR ofi.order_code =#{orderCode,jdbcType=VARCHAR}
UNION SELECT oons.order_code, oons.order_latest_status, oons.status_update_time, oons.status_create_time FROM ofc_cust_order_newstatus oons LEFT JOIN ofc_cust_fundamental_information ofi ON oons.order_code = ofi.order_code LEFT JOIN ofc_cust_distribution_basic_info odbi ON oons.order_code = odbi.order_code
OR ofi.order_code =#{orderCode,jdbcType=VARCHAR}
