mapper.select:
SELECT
oos.notes
FROM
ofc_order_status oos
LEFT JOIN ofc_fundamental_information ofi ON oos.order_code = ofi.order_code
LEFT JOIN ofc_distribution_basic_info odbi ON oos.order_code = odbi.order_code


    oos.order_code =#{orderCode}


    OR ofi.cust_order_code =#{custOrderCode}


    OR odbi.trans_code =#{transCode}


ORDER BY oos.lasted_oper_time ASC

SELECT oos.* FROM ( SELECT s.order_code, s.order_status, s.status_desc, s.lasted_oper_time, s.notes, s.operator, s.creation_time FROM ofc_order_status s, ( SELECT s.order_code, max(s.lasted_oper_time) val FROM ofc_order_status s GROUP BY s.order_code ) b WHERE s.order_code = b.order_code AND s.lasted_oper_time = b.val ORDER BY s.order_code ) oos LEFT JOIN ofc_fundamental_information ofi ON oos.order_code = ofi.order_code LEFT JOIN ofc_distribution_basic_info odbi ON oos.order_code = odbi.order_code
OR ofi.order_code =#{orderCode,jdbcType=VARCHAR}
OR ofi.cust_order_code =#{custOrderCode}
OR odbi.trans_code =#{transCode}
GROUP BY ofi.order_code

SELECT *
FROM ofc_order_status oos
WHERE oos.order_code = #{orderCode}

SELECT
oos.notes
FROM
ofc_order_status oos
LEFT JOIN ofc_fundamental_information ofi ON oos.order_code = ofi.order_code
LEFT JOIN ofc_distribution_basic_info odbi ON oos.order_code = odbi.order_code



      AND ofi.order_code = #{code}


      AND ofi.cust_order_code = #{code}


      AND odbi.trans_code = #{code}




ORDER BY oos.lasted_oper_time ASC
