mapper.select:
SELECT ost.index_num, ost.template_code, ost.template_type, ost.template_name, ost.cust_code, ost.cust_name, ost.standard_col_code, ost.standard_col_name, ost.reflect_col_name, ost.col_default_val, ost.creat_time, ost.creator, ost.creator_name, ost.oper_time, ost.operator, ost.operator_name FROM ofc_storage_template ost
AND ost.cust_name = #{custName, jdbcType=VARCHAR}
AND ost.template_type = #{templateType, jdbcType=VARCHAR}
AND ost.template_code = #{templateCode, jdbcType=VARCHAR}
AND ost.template_name LIKE concat(concat('%',#{templateName, jdbcType=VARCHAR}),'%')
GROUP BY ost.template_code

SELECT ost.index_num, ost.template_code, ost.template_type, ost.template_name, ost.cust_code, ost.cust_name, ost.standard_col_code, ost.standard_col_name, ost.reflect_col_name, ost.col_default_val, ost.creat_time, ost.creator, ost.creator_name, ost.oper_time, ost.operator, ost.operator_name FROM ofc_storage_template ost
AND ost.cust_name = #{custName, jdbcType=VARCHAR}
AND ost.template_type = #{templateType, jdbcType=VARCHAR}
AND ost.template_code = #{templateCode, jdbcType=VARCHAR}
AND ost.template_name LIKE concat(concat('%',#{templateName, jdbcType=VARCHAR}),'%')
ORDER BY ost.index_num
